$(function() {

  // Override active scaffold highlight effect
  ActiveScaffold.config = {
    "scroll_on_close": "checkInViewport",
    "highlight": {
      "color": "false"
    }
  };
  
  // Add delegated event listeners for the eye icon displayed in account
  // create/update form. When clicked, it will toggle the field type of the
  // password and password confirmation fields between text and password, such
  // that the operator may visually confirm spelling.
  $('body').on({
    click: function(e) {
      var eye = $(this);
      if (eye.hasClass('glyphicon-eye-open')) {
        eye.closest('form').find("input[name*='password']").attr({
          type: "text"
        })
        eye.removeClass('glyphicon-eye-open').addClass('glyphicon-eye-close');
      } else {
        eye.closest('form').find("input[name*='password']").attr({
          type: "password"
        })
        eye.removeClass('glyphicon-eye-close').addClass('glyphicon-eye-open');
      }
    }
  }, '[data-toggle="password_visibility"]');
  
})
