var sideMenu = {

  // Store the checkbox (sidemenutrigger) value when the sidemenu is open
  save: function() {

    // Check for HTML5 compatibility
    if (window.localStorage !== undefined) {
      var menubox = document.querySelectorAll('input[type=checkbox]');
      for (i = 0; i < menubox.length; i++) {
        localStorage.setItem(menubox[i].value, menubox[i].checked);
      }
    }

    // Trigger the window.resize event such that any charts 
    // on the page will automatically refresh their size
    window.dispatchEvent(new Event('resize'));
    
  },

  // Open the side menu if the user has opened it on previous navigations
  load: function() {

    // Check for HTML5 compatibility
    if (window.localStorage !== undefined) {
      // Find the checkboxes on the document and assign their local storage
      // state
      var menubox = document.querySelectorAll('#sidemenutrigger');
      for (i = 0; i < menubox.length; i++) {
        menubox[i].checked = localStorage.getItem(menubox[i].value) === 'true' ? true : false;
      }
    }
    
  }

}
